highp vec4 HSVToRGB(highp float hue, highp float saturation, highp float value, highp float a) {
  highp float huePrime = mod(hue,360.0);
  highp float c = (value/100.0) * (saturation/100.0);
  highp float x = c * (1.0 - abs(mod(huePrime/60.0, 2.0) - 1.0));
  highp float m = (value/100.0) - c;
  highp float r = 0.0;
  highp float g = 0.0;
  highp float b = 0.0;

  if (huePrime >= 0.0 && huePrime < 60.0) {
      r = c;
      g = x;
      b = 0.0;
  } else if (huePrime >= 60.0 && huePrime < 120.0) {
      r = x;
      g = c;
      b = 0.0;
  } else if (huePrime >= 120.0 && huePrime < 180.0) {
      r = 0.0;
      g = c;
      b = x;
  } else if (huePrime >= 180.0 && huePrime < 240.0) {
      r = 0.0;
      g = x;
      b = c;
  } else if (huePrime >= 240.0 && huePrime < 300.0) {
      r = x;
      g = 0.0;
      b = c;
  } else if (huePrime >= 300.0 && huePrime < 360.0) {
      r = c;
      g = 0.0;
      b = x;
  }
  r += m;
  g += m;
  b += m;
  return vec4(r, g, b, a);
}

highp vec4 HSVToRGB(highp vec4 colorInput) {
  return HSVToRGB(colorInput.x, colorInput.y, colorInput.z, colorInput.w);
}

highp vec4 RGBToHSV(highp float r, highp float g, highp float b, highp float a) {
  //Get the brightest and darkest channels
  highp float CMax = r;
  if (g > CMax) { CMax = g; }
  if (b > CMax) { CMax = b; }

  highp float CMin = r;
  if (g < CMin) { CMin = g; }
  if (b < CMin) { CMin = b; }

  highp float Delta = CMax - CMin;

  highp float H = 0.0;

  //Multiply and get the Hue
  if (CMax == r) {
      H = 60.0 * mod(((g - b) / Delta), 6.0);
  }
  if (CMax == g) {
      H = 60.0 * ((b - r) / Delta + 2.0);
  }
  if (CMax == b) {
      H = 60.0 * ((r - g) / Delta + 4.0);
  }

  //Set the saturation
  highp float S = 0.0;
  if (CMax != 0.0) {
      S = Delta / CMax;
  }

  return vec4(H, S * 100.0, CMax * 100.0, a);
}

highp vec4 RGBToHSV(highp vec4 colorInput) {
  return RGBToHSV(colorInput.x, colorInput.y, colorInput.z, colorInput.w);
}

